// SPDX-License-Identifier: MIT
pragma solidity ^0.5.16;
pragma experimental ABIEncoderV2;

contract HospitalsContract{



    struct Hospital{
        uint id;
        string name;
        string location;
        address director;
    }

    struct Director{
        string name;
        address walletAddress;
        uint hospital_id;  //0 if he is removed 
    }

    struct Doctor{
        address walletAddress;
        string name;
        string specialty;
        uint hospital_id;
        uint age;
        address[] pendingRequests;  // requests the doctor sent to patients
        address[] patientsList;
        address[] patientsRequests;   //requests recieved from patients
    }

    struct Patient{
        address patientWallet;
        string name;
        uint age;
        address[] authorizedDoctors;
        address[] doctorsRequests;
        address[] pendingRequests;
    }

    address owner; //supposed to be a responsible in the health ministry
    address[] Doctors;
    address[] Patients;
    address[] Directors;
    Hospital[] Hospitals;

    mapping(address => Director) directors;
    mapping(address => Doctor) doctors;
    mapping(address => Patient) patients;



    // constructor
    constructor() public {
        owner = msg.sender;
    }

    event DirectorReplaced(uint indexed hospitalID, address indexed oldDirector, address indexed newDirector);
    event DoctorRequestedAccess(address indexed _doctor, address indexed _patient);

    modifier OnlyOwner() {
        require (msg.sender == owner,'Caller should be hospital director!');
        _;
    }

    function isDirector(address _target) public view returns (bool) {
        for (uint i=0; i<Directors.length; i++){
            if(Directors[i] == _target && directors[Directors[i]].hospital_id>0){
                return true;
            }
        }
        return false;
    }

    modifier OnlyDirectors() {
        require(isDirector(msg.sender),'Caller should be a hospital director!');
        _;
    }


    function isDoctor(address _target) public view returns (bool){
        for (uint i=0; i<Doctors.length; i++){
            if(Doctors[i] == _target){
                return true;
            }
        }
        return false;
    }


    modifier OnlyDoctors() {
        require(isDoctor(msg.sender),"Caller should be a doctor!");
        _;
    }

    function isPatient(address _target) public view returns (bool){
        for (uint i=0; i<Patients.length; i++){
            if(Patients[i] == _target){
                return true;
            }
        }
        return false;
    }

    function validHospitalID(uint _id) public view returns (bool){
        if (_id>0 && _id<=Hospitals.length){
            return true;
        }
        else{
            return false;
        }
    }


    function isPatientInDoctor(address _doctor, address _patientAddress) public view returns (bool) {
        for (uint i = 0; i < doctors[_doctor].patientsList.length; i++) {
            if (doctors[_doctor].patientsList[i] == _patientAddress) {
                return true;
            }
        }
        return false;
    }

    function IsDoctorPending(address _doctor, address _patientAddress) public view returns (bool) {
        for (uint i = 0; i < patients[_patientAddress].doctorsRequests.length; i++) {
            if (patients[_patientAddress].doctorsRequests[i] == _doctor) {
                return true;
            }
        }
        return false;
    }




    function removeAddressFromArray(address[] memory addressesArray, address addressToRemove) public pure returns (address[] memory) {
        uint length = addressesArray.length;
        uint indexToRemove = 0;
        bool found = false;

        // Find the index of the address to remove
        for (uint i = 0; i < length; i++) {
            if (addressesArray[i] == addressToRemove) {
                indexToRemove = i;
                found = true;
                break;
            }
        }

        // If the address is not found, return the original array
        if (!found) {
            return addressesArray;
        }

        // Create a new array to hold the result
        address[] memory newArray = new address[](length - 1);

        // Copy elements from the original array to the new array, excluding the address to remove
        for (uint i = 0; i < indexToRemove; i++) {
            newArray[i] = addressesArray[i];
        }
        for (uint i = indexToRemove + 1; i < length; i++) {
            newArray[i - 1] = addressesArray[i];
        }

        return newArray;
    }









    modifier DirectorOfDoctor(address _doctor) {
        require(isDirector(msg.sender),"Not a valid Director!");
        require(isDoctor(_doctor),"Not a valid Doctor!");
        require(directors[msg.sender].hospital_id == doctors[_doctor].hospital_id,"Doctor works in another hospital");
        _;
    }

    //function to find director index in Directors given his address
    function directorIndex(address _director) public view returns(uint) {
        for (uint i=0; i<Directors.length; i++){
            if (Directors[i] == _director){
                return i;
            }
        }
        return Directors.length;
    }











    //                      functions of the owner of the contract


    //function to replace a director with another one
    function replaceDirector(uint _id, string memory _name, address newDirectorAddress) public OnlyOwner {
        require(validHospitalID(_id),"No registered hospital with the provided ID");
        address oldDirector = Hospitals[_id-1].director;  //save address of the ancient director for event emission
        uint index = directorIndex(oldDirector); //his index in the array of Directors
        Director memory newDirector = Director(_name, newDirectorAddress, _id); //instantiate the new director
        directors[newDirectorAddress] = newDirector; //add new director to the mapping of dircetors 
        Hospitals[_id-1].director = newDirectorAddress; //update the director address of the hospital
        directors[oldDirector].hospital_id = 0;  //set hospital ID of ancient director to 0 as he no longer works there
        Directors[index] = newDirectorAddress; //add the new director to the array

        emit DirectorReplaced(_id, oldDirector, newDirectorAddress); //emit the event of replacement
    }



    //event of hospital creation
    event createdHospital(string _name);



    //function to add a hospital and appoint a director to it
    function addHospital(string memory _name, string memory _location, address _directorAddress, string memory _directorName) public OnlyOwner{
        Director memory newDirector = Director(_directorName, _directorAddress, Hospitals.length + 1);  //instantiate the new director
        directors[_directorAddress] = newDirector; //add the new director to the mapping of directors
        Directors.push (_directorAddress);
        Hospital memory newHospital = Hospital(Hospitals.length+1, _name, _location, _directorAddress);   // instantiate the new hospital
        Hospitals.push(newHospital);
        emit createdHospital(_name);
    }


    //function to get the hospitals array
    function getHospitals() public view returns(Hospital[] memory){
        return Hospitals;
    } 














    //                                      functions of the director



    //function to add a new doctor
    function addDoctor(address _walletAddress, string memory _name, string memory _specialty, uint _age) public OnlyDirectors{
        uint _hospital_id = directors[msg.sender].hospital_id;
        address[] memory _pendingRequests;
        address[] memory _patientsList;
        address[] memory _patientsRequests;

        Doctor memory newDoctor = Doctor({
            walletAddress: _walletAddress,
            name: _name,
            specialty: _specialty,
            hospital_id: _hospital_id,
            age: _age,
            pendingRequests: _pendingRequests,
            patientsList: _patientsList ,
            patientsRequests: _patientsRequests
        });

        Doctors.push(_walletAddress);
        doctors[_walletAddress] = newDoctor;  
    }


    //function to transfer a doctor from one hospital to another

function transferDoctor(address _doctor, uint newHospital) public DirectorOfDoctor(_doctor) {
    require(validHospitalID(newHospital));
    doctors[_doctor].hospital_id = newHospital;
}


//function to get the list of doctors
function getDoctors() public view returns(address[] memory){
        return Doctors;
    } 








//                        functions of the doctor

// function to to request access from a patient
function requestAccess(address _patient) public{
    require (isPatient(_patient), 'Not a registered patient!');
    require(isDoctor(msg.sender),'Caller is not a registerd doctor!');
    require(isPatientInDoctor(msg.sender, _patient),"You already have access to this patient's data");
    require(!IsDoctorPending(msg.sender, _patient),"You already sent a request to this patient");
    patients[_patient].doctorsRequests.push(msg.sender);
    doctors[msg.sender].pendingRequests.push(_patient);

}


//function to accept a patient
function acceptPatient(address _patient) public {
    doctors[msg.sender].patientsList.push(_patient);
    doctors[msg.sender].patientsRequests = removeAddressFromArray(doctors[msg.sender].patientsRequests, _patient);
    patients[_patient].authorizedDoctors.push(msg.sender);
    patients[_patient].pendingRequests = removeAddressFromArray(patients[_patient].pendingRequests, msg.sender);

}

// function to reject a patient
function rejectPatient( address _patient) public{
    patients[_patient].pendingRequests = removeAddressFromArray(patients[_patient].pendingRequests, msg.sender);
    doctors[msg.sender].patientsRequests = removeAddressFromArray(doctors[msg.sender].patientsRequests, _patient);

}











}












